ALBUM_IDS                       = "" # ID альбомов; записываются через запятые без пробелов; пустая строка возьмет все альбомы;
IMAGE_SIZE_TYPE                 = "z" # Размер изображения, загружаемый с сайта ВК, подробнее можно почитать в документации: https://dev.vk.com/reference/objects/photo-sizes#type
LOGIN_DATA_PATH                 = "../LoginData/login.json" # путь к файлу с данными для авторизации;
FACIAL_POINTS_MODEL             = "large" # small/large; количество распознаемых точек на лице; small -- быстрее, но ниже точность (5 точек); large -- медленее, но больше точность (68 точек);
FACE_BOXES_MODEL                = "cnn" # hog/cnn; модель для распознавания лиц; hog -- быстрее, но ниже точность; cnn -- медленее, но больше точность (рекомендуется иметь GPU);
NUMBER_OF_ATTEMPTS              = 3 # количество повторных попыток загрузить изображение, на случай проблем с подключением к интернету;
NUM_JITTERS                     = 4 # чем больше значение, тем медленнее скорость и больше точность;
OLDEST_ALBUM_UNIX_TIME          = -1 # нижняя граница по времени, для фильтрации старых альбомов, указывается количество секунд с 1 января 1970 года;
OWNER_ID                        = "" # ID пользователя/сообщества, который владеет альбомом;
LIST_PHOTOS_PICKLE_FILENAME     = "list_photos.pickle" # название файла, в которую сохраняются ссылки на фотографии;
RESTORE_PHOTO_URLS_FROM_PICKLE  = False # False -- при каждом запуске скрипт обращается к серверам ВК за ссылками на фотографии и сохраняет их в файл; True -- скрипт не обращается к серверам, а берет ссылки из файла;
IMG_URLS_OFFSET                 = 0 # сдвиг в списке со ссылками на фотографии (используется, например, при ошибке во время обработки изображений);
SUPPRESS_NOT_FOUND_IN_ALBUMS    = True # True -- убирает вывод сообщения о том, что лица не были найдены в фотографии
TOLERANCE                       = 0.5 # чем меньше, тем выше точность, однако может перестать распознавать похожие лица;